import React, { useRef, useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";

function App() {
  const nameRef = useRef(null);
  const ageRef = useRef(null);
  const countryRef = useRef(null);
  const positionRef = useRef(null);
  const wageRef = useRef(null);

  const [obj, setObj] = useState({
    name: "",
    age: 0,
    country: "",
    position: "",
    wage: 0,
  });

  const [loading, setLoading] = useState(true);

  const [employeeList, setEmployeeList] = useState([]);

  const [show, setShow] = useState(false);

  const [isEdit, setIsEdit] = useState(true);

  const loadEmployees = async () => {
    await Axios.get("http://localhost:3001/get")
      .then((res) => res.json())
      .then((res) => {
        setEmployeeList(res.data);
        setLoading(false);
      });
  };

  useEffect(() => {
    loadEmployees();
  }, []);

  const resetValues = () => {
    nameRef.current.value = "";
    ageRef.current.value = "";
    countryRef.current.value = "";
    positionRef.current.value = "";
    wageRef.current.value = "";

    setObj({
      name: "",
      age: 0,
      country: "",
      position: "",
      wage: 0,
    });
  };

  const submitHandler = (e) => {
    e.preventDefault();

    resetValues();

    Axios.post("http://localhost:3001/create", {
      data: obj,
    }).then(() => {
      console.log("Post Success");
      loadEmployees();
      setEmployeeList((prev) => {
        return [...prev, obj];
      });
    });
  };

  const deleteHandler = (id) => {
    Axios.delete(`http://localhost:3001/remove/${id}`).then(() => {
      setEmployeeList(employeeList.filter((item) => item.id !== id));
      loadEmployees();
      console.log(employeeList);
    });
  };

  const editHandler = (id) => {
    Axios.get(`http://localhost:3001/get/${id}`).then((res) => {
      const data = res.data;
      nameRef.current.value = data.name;
      ageRef.current.value = data.age;
      countryRef.current.value = data.country;
      positionRef.current.value = data.position;
      wageRef.current.value = data.wage;
      setIsEdit = (prev) => !prev;
    });
  };

  const updateHandler = (id) => {};

  return (
    <>
      {!loading && (
        <div className="App">
          <form action="submit">
            <label htmlFor="input">Name:</label>
            <input
              htmlFor="input"
              onChange={(e) => {
                setObj((prev) => {
                  return { ...prev, name: e.target.value };
                });
              }}
              ref={nameRef}
            />
            <label htmlFor="input">Age:</label>
            <input
              type="number"
              onChange={(e) => {
                setObj((prev) => {
                  return { ...prev, age: e.target.value };
                });
              }}
              ref={ageRef}
            />
            <label htmlFor="input">Country:</label>
            <input
              type="text"
              onChange={(e) => {
                setObj((prev) => {
                  return { ...prev, country: e.target.value };
                });
              }}
              ref={countryRef}
            />
            <label htmlFor="input">Position:</label>
            <input
              type="text"
              onChange={(e) => {
                setObj((prev) => {
                  return { ...prev, position: e.target.value };
                });
              }}
              ref={positionRef}
            />
            <label htmlFor="input">Wage (year):</label>
            <input
              type="number"
              onChange={(e) => {
                setObj((prev) => {
                  return { ...prev, wage: e.target.value };
                });
              }}
              ref={wageRef}
            />
            <button onClick={submitHandler} type="submit">
              Submit
            </button>
            <button
              className="show"
              onClick={(e) => {
                e.preventDefault();
                setShow((prev) => !prev);
              }}
            >
              Show
            </button>
          </form>
          <div className="employees">
            {show &&
              employeeList.map((employee) => {
                return (
                  <div className="employee">
                    <ul>
                      <li>Name: {employee.name}</li>
                      <li>Age: {employee.age}</li>
                      <li>Country: {employee.country}</li>
                      <li>Position: {employee.position}</li>
                      <li>Wage: {employee.wage}</li>
                    </ul>
                    <button
                      onClick={
                        isEdit
                          ? () => editHandler(employee.id)
                          : updateHandler(employee.id)
                      }
                    >
                      {isEdit ? "Edit" : "Update"}
                    </button>
                    <button onClick={() => deleteHandler(employee.id)}>
                      Delete
                    </button>
                  </div>
                );
              })}
          </div>
        </div>
      )}
    </>
  );
}

export default App;
