import React, { useRef, useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";

function App() {
  const nameRef = useRef(null);
  const ageRef = useRef(null);
  const countryRef = useRef(null);
  const positionRef = useRef(null);
  const wageRef = useRef(null);

  const [obj, setObj] = useState({
    name: "",
    age: 0,
    country: "",
    position: "",
    wage: 0,
  });

  const submitHandler = (e) => {
    e.preventDefault();
    setObj((prev) => {
      return {
        ...prev,
        name: nameRef?.current.value,
        age: ageRef?.current.value,
        country: countryRef?.current.value,
        position: positionRef?.current.value,
        wage: wageRef?.current.value,
      };
    });
  };

  // useEffect(() => {
  //   Axios.post("https://localhost:3001/create", {
  //     name: obj.name,
  //     age: obj.age,
  //     country: obj.country,
  //     positionRef: obj.position,
  //     wage: obj.wage,
  //   }).then((response) => () => () => {
  //     console.log("Success");
  //   });
  // }, [obj]);

  return (
    <div className="App">
      <form action="submit" onSubmit={submitHandler}>
        <label htmlFor="input">Name:</label>
        <input
          htmlFor="input"
          onChange={(e) => {
            setObj((prev) => {
              return { ...prev, name: e.target.value };
            });
          }}
        />
        <label htmlFor="input">Age:</label>
        <input type="number" ref={ageRef} />
        <label htmlFor="input">Country:</label>
        <input type="text" ref={countryRef} />
        <label htmlFor="input">Position:</label>
        <input type="text" ref={positionRef} />
        <label htmlFor="input">Wage (year):</label>
        <input type="number" ref={wageRef} />
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
